    @SuppressWarnings("empty-statement")
    private void jButtonPuntoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonPuntoActionPerformed
        if (resultadomostrado == true) {
        jTextArea1.setText("");
        resultadomostrado = false;
    }
    String operacion = jTextArea1.getText();
    if (!operacion.contains("+") && !operacion.contains("-") && !operacion.contains("/") && !operacion.contains("*")) {
        if (operacion.contains(".")) {
            return;
        }
    }
    boolean contienePunto = false;
    String[] operaciones = {"+" , "-", "/", "*"};
    for (String ope : operaciones) {
        int indexOperacion = operacion.lastIndexOf(ope);
        if (indexOperacion != -1) {
            String parteDespuesOperacion = operacion.substring(indexOperacion + 1);
            if (parteDespuesOperacion.contains(".")) {
                contienePunto = true;
                break;
            }
        }
    }
    if (!contienePunto && !operacion.endsWith(".")) {
        jTextArea1.append(".");
    }
    }//GEN-LAST:event_jButtonPuntoActionPerformed

