    private void jButtonIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonIgualActionPerformed
        String operacion = jTextArea1.getText();
        String[] operaciones = {"+" , "-", "/", "*"};
        String decimal = ".";
        int indice; 
        int num1; 
        int num2; 
        String resultado; 
                for(String ope: operaciones){
                    switch (ope){

                        case "+":
                            if(operacion.contains(ope)){
                               indice = operacion.indexOf('+');
                               if(operacion.contains(decimal)){
                                    double num1decimal = Double.parseDouble(operacion.substring(0, indice));
                                    double num2decimal = Double.parseDouble(operacion.substring(indice + 1));
                                    resultado = Double.toString(num1decimal + num2decimal);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                               }else {
                                    num1 = Integer.parseInt(operacion.substring(0, indice));
                                    num2 = Integer.parseInt(operacion.substring(indice + 1)); 
                                    resultado = Integer.toString(num1 + num2);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;    
                                }

                            }
                            break;
                        case "-":
                            if(operacion.contains(ope)){
                               indice = operacion.indexOf('-');
                               if(operacion.contains(decimal)){
                                    double num1decimal = Double.parseDouble(operacion.substring(0, indice));
                                    double num2decimal = Double.parseDouble(operacion.substring(indice + 1));
                                    resultado = Double.toString(num1decimal - num2decimal);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                               }else {
                                    num1 = Integer.parseInt(operacion.substring(0, indice));
                                    num2 = Integer.parseInt(operacion.substring(indice + 1)); 
                                    resultado = Integer.toString(num1 - num2);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                               }
                            }
                            break;
                        case "/":
                            if(operacion.contains(ope)){
                                try{
                                    indice = operacion.indexOf('/');
                                    Double num1division = Double.valueOf(operacion.substring(0, indice));
                                    Double num2division = Double.valueOf(operacion.substring(indice + 1));
                                    double division = num1division / num2division;
                                    resultado = String.format("%.2f",division);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                                }catch(NumberFormatException e){
                                    jTextArea1.setText("!Division por 0!");
                                }
                            }   
                            break;
                        case "*":
                            if(operacion.contains(ope)){
                               indice = operacion.indexOf('*');
                               if(operacion.contains(decimal)){
                                    double num1decimal = Double.parseDouble(operacion.substring(0, indice));
                                    double num2decimal = Double.parseDouble(operacion.substring(indice + 1));
                                    resultado = Double.toString(num1decimal * num2decimal);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                               }else {
                                    num1 = Integer.parseInt(operacion.substring(0, indice));
                                    num2 = Integer.parseInt(operacion.substring(indice + 1)); 
                                    resultado = Integer.toString(num1 * num2);
                                    jTextArea1.setText(resultado);
                                    resultadomostrado = true;
                               }
                            }
                            break;
                    }
                }
    }//GEN-LAST:event_jButtonIgualActionPerformed

